CREATE FUNCTION [m2ss].[time_to_sec](@time DATETIME) RETURNS INT
AS
  BEGIN
        RETURN DATEPART(HOUR, @time) * 3600 +
               DATEPART(MINUTE, @time) * 60 + DATEPART(SECOND, @time)
  END
