CREATE FUNCTION [dbo].[shiftLeft] (
	@x	INT,
	@s	INT
)
	RETURNS TABLE
	WITH SCHEMABINDING
AS
RETURN
	SELECT
		-- Equivalent to x * 2^s
		-- Truncate bits 32-63 and cast back to INT
		y = CAST(CAST(@x * p.pow AS BINARY(4)) AS INT)
	FROM (
		-- 2^31 will overflow, cast to BIGINT
		SELECT pow = POWER(CAST(2 AS BIGINT), @s & 0x1F) -- Wrap around 31
	) p

