CREATE FUNCTION [dbo].[SUBSTRING_INDEX]
(
    @str VARCHAR(8000),
    @delim VARCHAR(1),
    @count INT
)
RETURNS VARCHAR(8000)
WITH SCHEMABINDING
BEGIN
    DECLARE @XmlSourceString XML;
    SET @XmlSourceString = (SELECT '<root><row>' + REPLACE( (SELECT @str AS '*' FOR XML PATH('')) , @delim, '</row><row>' ) + '</row></root>');

    RETURN STUFF
    (
        ((
            SELECT  @delim + x.XmlCol.value('(text())[1]', 'VARCHAR(8000)') AS '*'
            FROM    @XmlSourceString.nodes('(root/row)[position() <= sql:variable("@count")]') x(XmlCol)
            FOR XML PATH(''), TYPE
        ).value('.', 'VARCHAR(8000)')), 
        1, 1, ''
    );
END
