CREATE PROCEDURE [dbo].[ShowAndDeleteAchievementDuplicate]
AS
BEGIN
	
	DECLARE @lastIDs TABLE (id BIGINT);
	DECLARE @ff TABLE (id_achievement BIGINT, id_achievementm BIGINT, [UID] INT, typeid INT, dup BIGINT);

	INSERT INTO @ff SELECT TOP (10000)
		MAX([ID_ACHIEVEMENT]) AS [ID_ACHIEVEMENT],
		MIN([ID_ACHIEVEMENT]) AS [ID_ACHIEVEMENTM],
		[UID]
		,[TypeID]
		, COUNT_BIG(*) AS DUP
	FROM [pangya].[pangya_achievement]
	WHERE [TypeID] < 0x78000000
	GROUP BY [TypeID], [UID]
	HAVING COUNT_BIG(*) > 1
	ORDER BY [UID]

	-- SHOW
	SELECT * FROM @ff

	-- DELETE
	DELETE FROM pangya.pangya_quest WHERE [achievement_id] in(SELECT id_achievement FROM @ff)
	DELETE FROM pangya.pangya_achievement WHERE ID_ACHIEVEMENT in(SELECT ID_ACHIEVEMENT FROM @ff)
END
GO
