*   SSMA informational messages:
*   M2SS0003: The following SQL clause was ignored during conversion:
*   DEFINER = `root`@`localhost`.
*/

CREATE PROCEDURE [pangya].[FLUSHTUTORIAL]  
   @IDUSER int,
   @TIPO int,
   @VALOR int
AS 
   BEGIN

      SET  XACT_ABORT  ON

      SET  NOCOUNT  ON

      IF @TIPO = 0
         BEGIN

            UPDATE pangya.tutorial
               SET 
                  Rookie = tutorial.Rookie + @VALOR
            WHERE tutorial.UID = @IDUSER

            EXECUTE pangya.ProcDestribuiItemTutorial @IDUSER, @VALOR, 0

         END
      ELSE 
         IF @TIPO = 1
            BEGIN

               UPDATE pangya.tutorial
                  SET 
                     Rookie = tutorial.Rookie + @VALOR
               WHERE tutorial.UID = @IDUSER

               UPDATE pangya.account
                  SET 
                     doTutorial = 1
               WHERE account.UID = @IDUSER

               EXECUTE pangya.ProcDestribuiItemTutorial @IDUSER, @VALOR, 0

               EXECUTE pangya.Evento1 @IDUSER

            END
         ELSE 
            BEGIN
               IF @TIPO = 256
                  BEGIN

                     DECLARE
                        @TESTE int

                     UPDATE pangya.tutorial
                        SET 
                           Beginner = tutorial.Beginner + @VALOR
                     WHERE tutorial.UID = @IDUSER

                     EXECUTE pangya.ProcDestribuiItemTutorial @IDUSER, @VALOR, 1

                     SELECT @TESTE = tutorial.Beginner
                     FROM pangya.tutorial
                     WHERE tutorial.UID = @IDUSER

                     IF @TESTE = 16128
                        BEGIN

                           UPDATE pangya.account
                              SET 
                                 doTutorial = 1
                           WHERE account.UID = @IDUSER

                           EXECUTE pangya.Evento2 @IDUSER

                        END

                  END
            END

   END
GO
