-- =============================================
-- Author:		<Author,,Name>
-- Create date: <Create Date,,>
-- Description:	<Description,,>
-- =============================================
CREATE PROCEDURE [pangya].[ProcChangeNickname]
	-- Add the parameters for the stored procedure here
	@UID INT
AS
BEGIN
	
	DECLARE @RET INT = -1
	DECLARE @CP BIGINT = 0

	IF @UID > 0
	BEGIN

		IF (SELECT FIRST_LOGIN FROM pangya.account WHERE [UID] = @UID) = 1
		BEGIN

			IF (SELECT IIF(change_nick IS NULL, 1, DATEDIFF(MONTH, change_nick, getdate())) as diff FROM pangya.account WHERE [UID] = @UID) > 0
			BEGIN
		
				IF (SELECT cookie FROM pangya.user_info WHERE [uid] = @UID) >= 60
				BEGIN

					-- Update cookie, tira os 60 CP que gastar para trocar o nickname
					UPDATE pangya.user_info SET Cookie = Cookie - 60 WHERE [UID] = @UID;

					-- Atualiza CP que vai ser retornado
					SET @CP = (SELECT cookie FROM pangya.user_info WHERE [uid] = @UID);

					-- Insere o nickname antigo no log de change nickname
					INSERT INTO pangya.pangya_change_nickname_log([UID], nickname) SELECT TOP(1) [UID], NICK FROM pangya.account WHERE [UID] = @UID

					-- Atualiza a flag de first login e a data que o player pediu para trocar de nickname
					UPDATE pangya.account 
						SET FIRST_LOGIN = 0,			-- Ele pode trocar o nick quando ele logar no jogo
							change_nick = getdate()		-- Atualiza a data que ele atualizou o nickname
					WHERE [UID] = @UID;

					SET @RET = 1;	-- Fez o pedido de troca de nickname com sucesso

				END
				ELSE
					SET @RET = -2;	-- Não tem cookies suficiente

			END
			ELSE
				SET @RET = -3;	-- Ainda não deu um mês desde que ele trocou o nickname

		END
		ELSE
			SET @RET = -4;	-- Já pediu para trocar o nickname

	END

	SELECT @RET as RET, @CP as cookie
END
