-- =============================================
-- Author:		<Author,,Name>
-- Create date: <Create Date,,>
-- Description:	<Description,,>
-- =============================================
CREATE PROCEDURE [pangya].[ProcChangePassword]
	-- Add the parameters for the stored procedure here
	@UID INT,
	@PWD VARCHAR(33)
AS
BEGIN
	
	DECLARE @RET INT = -1
	DECLARE @EMAIL NVARCHAR(100) = N''

	IF @UID > 0 AND @PWD != ''
	BEGIN

		-- Pega email do player
		SET @EMAIL = (SELECT email FROM pangya.contas_beta WHERE [UID] = @UID)

		-- Verifica se tem log de troca de senha
		IF (SELECT [UID] FROM pangya.pangya_change_pwd_log WHERE [UID] = @UID) IS NOT NULL
		BEGIN

			DECLARE @COUNT INT = 0
			DECLARE @CHANGE_DATE DATETIME2

			-- Pega o contador de vez que o player trocou o login e a primeira data que ele trocou
			SELECT @COUNT = [count], @CHANGE_DATE = change_date
			FROM pangya.pangya_change_pwd_log
			WHERE [UID] = @UID
			
			IF DATEDIFF(MINUTE, @CHANGE_DATE, getdate()) >= 1440 -- 24 horas em minutos
			BEGIN
			
				-- Faz mais de 24 horas que ele trocou de senha	
				UPDATE pangya.pangya_change_pwd_log 
					SET last_change = getdate(),
						change_date = getdate(),
						[count] = 1
				WHERE [UID] = @UID

				-- Troca a senha
				UPDATE pangya.account SET [PASSWORD] = @PWD WHERE [UID] = @UID

				SET @RET = 1

			END
			ELSE IF @COUNT = 1
			BEGIN
				
				-- Menos de 24 horas que ele trocou de senha uma vez
				UPDATE pangya.pangya_change_pwd_log
					SET last_change = getdate(),
						[count] = @COUNT + 1
				WHERE [UID] = @UID

				-- Troca a senha
				UPDATE pangya.account SET [PASSWORD] = @PWD WHERE [UID] = @UID

				SET @RET = 1

			END
			ELSE
				SET @RET = -2

		END
		ELSE
		BEGIN
			
			-- Ele não tem log de troca de senha, cria um novo para ele
			INSERT INTO pangya.pangya_change_pwd_log([UID]) VALUES(@UID)

			-- Troca a senha
			UPDATE pangya.account SET [PASSWORD] = @PWD WHERE [UID] = @UID

			SET @RET = 1

		END
		
	END

	SELECT @RET AS RET, @EMAIL AS EMAIL
END
GO
