CREATE PROCEDURE [pangya].[ProcCheckRescuePasswordAndIDKey]
	-- Add the parameters for the stored procedure here
	@INDEX BIGINT,
	@KEY NVARCHAR(100)
AS
BEGIN

	DECLARE @RET INT = -1
	DECLARE @UID INT = 0
	DECLARE @TIPO TINYINT = 0
	
	IF @INDEX > 0 AND @KEY != N''
	BEGIN

		SET @UID = (SELECT [UID] FROM pangya.pangya_rescue_pwd_log WHERE [index] = @INDEX AND key_uniq = @KEY AND [state] = 0 AND DATEADD(HOUR, 1, send_date) > getdate())

		IF @UID IS NOT NULL AND @UID > 0
		BEGIN

			-- Pega o tipo de recuperação se é de senha ou de id
			SELECT @TIPO = tipo FROM pangya.pangya_rescue_pwd_log WHERE [index] = @INDEX AND [UID] = @UID

			-- Atualiza o state da chave para utilizada
			IF @TIPO = 2 -- ID Faz agora por que ele só precisa ver o id, e não trocar ele como a senha
				UPDATE pangya.pangya_rescue_pwd_log SET [state] = 1 WHERE [index] = @INDEX AND [UID] = @UID

			-- Verificou a chave com sucesso
			SET @RET = 1

		END
		ELSE
			SET @RET = -2	-- Chave invalida, não existe a chave, já foi utilizada ou  ela expirou

	END

	SELECT @RET AS RET, @UID as [UID], @TIPO AS TIPO

END
