-- =============================================
-- Author:		<Author,,Name>
-- Create date: <Create Date,,>
-- Description:	<Description,,>
-- =============================================
CREATE PROCEDURE [pangya].[ProcDo1stAnniversary]
AS
BEGIN
	
	-- Check if event 1st aniversary was finished
	IF NOT EXISTS (SELECT TOP 1 EVENT_DONE FROM pangya.pangya_1st_anniversary WHERE EVENT_DONE = 1)
	BEGIN

		DECLARE @UID INT,
				@LOGIN_DAYS INT,
				@CP BIGINT,
				@APT BIGINT = 0,
				@WIN BIGINT = 0,
				@MSG NVARCHAR(500),
				@MSGID INT

		-- All players apt
		SELECT 
			@APT = COUNT_BIG([counter]) 
		FROM 
			pangya.pangya_attendance_reward a
			INNER JOIN
			pangya.user_info b
			ON a.[UID] = b.[UID]

		-- Make cursor to all players win CP in 1st aniversary event
		DECLARE BIRTHDAY_EVENT CURSOR FOR
			SELECT 
				a.[UID], a.[COUNTER] 
			FROM 
				pangya.pangya_attendance_reward a
				INNER JOIN
				pangya.user_info b
				ON a.[UID] = b.[UID]
			WHERE a.[COUNTER] >= 10

		OPEN BIRTHDAY_EVENT
		FETCH NEXT FROM BIRTHDAY_EVENT INTO @UID, @LOGIN_DAYS
		
		WHILE @@FETCH_STATUS = 0
		BEGIN

			SET @MSGID = 0
			
			-- CP Insert
			SET @CP = @LOGIN_DAYS * 10

			SET @MSG = CONCAT(N'First anniversary celebration, You logged ', @LOGIN_DAYS, N' days and won ', @CP, N'CP.')

			INSERT INTO pangya.pangya_gift_table([UID], fromid, [message], giftdate, Lida_YN, valid, [Flag], Contador_Vista)
			VALUES(@UID, '@SUPERSS', @MSG, getdate(), 0, 1, 0, 0)

			SET @MSGID = @@IDENTITY

			IF @MSGID > 0
			BEGIN

				-- INSERT Cookie Point to player
				UPDATE pangya.user_info SET Cookie = Cookie + @CP WHERE [UID] = @UID

				-- Log
				INSERT INTO pangya.pangya_1st_anniversary_player_win_cp([UID], LOGIN_DAYS, COOKIE_POINT) VALUES(@UID, @LOGIN_DAYS, @CP)

				-- Comando para mostrar no jogo que o player recebeu um presente
				EXEC pangya.ProcInsertCommand 4, @UID, @MSGID, 0, 0, 0, 1, 0, 1, null

			END -- IF MSG IS IS OK
			ELSE
				PRINT CONCAT(N'Failed to make gift table message to player[UID=', @UID, N']')

			-- Count Win player
			SET @WIN = @WIN + 1

			-- NEXT ROW
			FETCH NEXT FROM BIRTHDAY_EVENT INTO @UID, @LOGIN_DAYS

		END -- End While

		-- Close cursor
		CLOSE BIRTHDAY_EVENT
		DEALLOCATE BIRTHDAY_EVENT
			
		-- Finish Event 1st aniversary
		IF (SELECT EVENT_DONE FROM pangya.pangya_1st_anniversary) IS NULL
			INSERT INTO pangya.pangya_1st_anniversary(EVENT_DONE, ALL_PLAYER_APT, ALL_PLAYER_WIN) VALUES(1, @APT, @WIN)
		ELSE
			UPDATE pangya.pangya_1st_anniversary SET EVENT_DONE = 1, ALL_PLAYER_APT = @APT, ALL_PLAYER_WIN = @WIN

		PRINT N'Event 1s Anniversary was finished succeful'

	END
	ELSE
	BEGIN
		DECLARE @MSG_ERR NVARCHAR(500)

		SELECT TOP 1 @MSG_ERR = CONCAT(N'Event 1s Anniversary was finished in ', REG_DATE) FROM pangya.pangya_1st_anniversary

		PRINT @MSG_ERR
	END
END
