CREATE PROCEDURE [pangya].[ProcExchangeCookiePointByEpin]
	@EPIN_ID BIGINT,
	@UID INT,
	@VALUE BIGINT
AS
BEGIN
	
	IF @EPIN_ID > 0 AND @UID > 0 AND @VALUE > 0
	BEGIN

		-- Update Epin to valid = 0, já trocou o epin e o uid de quem trocou o epin por CP
		UPDATE pangya.pangya_donation_epin
			SET
				[retrive_uid] = @UID,
				[valid] = 0
		WHERE
			[index] = @EPIN_ID

		-- Adiciona os CPs para o player
		UPDATE pangya.user_info
			SET
				[Cookie] = [Cookie] + @VALUE
		WHERE
			[UID] = @UID

		-- CP Log
		INSERT INTO pangya.pangya_cookie_point_log([UID], [TYPE], [mail_id], [cookie], [item_qnty])
		VALUES(@UID, 4/*ADD CP FROM EPIN DONATION*/, -1, @VALUE, 0)

		SELECT @EPIN_ID AS ID

	END
	ELSE
		SELECT -1 AS ID
END
