CREATE PROCEDURE [pangya].[ProcGachaBuyCoin]
	-- Add the parameters for the stored procedure here
	@IDUSER INT,
	@COIN_ID INT
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	IF @COIN_ID > 0 AND @IDUSER > 0
	BEGIN
		DECLARE @PRICE INT,
				@COOK INT,
				@COIN_TYPEID INT = 436207832

		SET @price = (SELECT a.preco FROM pangya.pangya_gacha_coin a, pangya.pangya_gacha b WHERE a.typeid = @COIN_ID AND a.tipo = b.coin)
        SET @cook = (SELECT cookie FROM pangya.user_info WHERE [UID] = @IDUSER)
        
		IF ((@cook - @price) >= 0)
		BEGIN
			DECLARE @COIN_QNTD INT,
					@COINID INT

			SET @coin_qntd = (SELECT a.qntd FROM pangya.pangya_gacha_coin a, pangya.pangya_gacha b WHERE a.typeid = @COIN_ID AND a.tipo = b.coin)
            
			SET @COINID = ISNULL((SELECT TOP 1 item_id FROM pangya.pangya_item_warehouse WHERE [UID] = @IDUSER AND typeid = @COIN_TYPEID), -1)

			IF @COINID = -1	-- ADD NEW ITEM
				exec pangya.ProcAddItem @IDUSER, 0, 0, -1, @COIN_TYPEID, 0, 0, @coin_qntd, 0, 0, 0, 0
			ELSE
			BEGIN	--	 UPDATE COUNT
				UPDATE pangya.pangya_item_warehouse SET c0 = c0 + @COIN_QNTD WHERE [UID] = @IDUSER AND item_id = @COINID
				SELECT @COINID as ITEM_ID
			END
            
            UPDATE pangya.user_info SET cookie = cookie - @price WHERE [UID] = @IDUSER
            
            -- Sistema de scratchy por points gasto
			DECLARE @QNTD_SCRATCHY INT

            SET @qntd_scratchy = FLOOR(@price / 1000)
            
            SET @qntd_scratchy = @qntd_scratchy + CASE WHEN @price % 1000 >= 500 THEN 1 ELSE 0 END
            
            --# rate do server de scratchy
            SET @qntd_scratchy = @qntd_scratchy * FLOOR((SELECT ScratchyPorPointRate FROM pangya.pangya_config) / 100)
            
            INSERT INTO pangya.pangya_gift_table(UID, FROMID, MESSAGE, GIFTDATE, LIDA_YN, VALID)
					VALUES(@IDUSER, N'@SUPERSS', N'Pangya Super Agracede ^.^', getdate(), 0, 1)

			DECLARE @MSGID INT

			SET @MSGID = @@IDENTITY
            
            exec pangya.ProcInsertItemNoEmail 0, @IDUSER, @MSGID, -1, 436207667, 0, @qntd_scratchy, 0
            
            UPDATE pangya.pangya_gacha_user_key SET att_flag = 1 WHERE [UID] = @IDUSER
		END
		ELSE
			SELECT 0 as ITEM_ID

	END
	ELSE
		SELECT 0 as ITEM_ID
END
GO
