	@id_in NVARCHAR(22),
	@pass NVARCHAR(32)
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	DECLARE @IDUSER INT,
			@returnvalue INT

	exec pangya.CheckWeblink_Key$IMPL @id_in, @pass, @returnvalue = @returnvalue OUTPUT

	SET @IDUSER = @returnvalue
    
	IF @IDUSER >= 0
	BEGIN
		DECLARE @KEY_ NVARCHAR(22)
		DECLARE @coin_count INT

		SET @KEY_ = upper(convert(varchar(22), hashbytes('md5', convert(varchar(32), rand(), 2)), 2))

        SET @coin_count = ISNULL((SELECT COUNT(c0) as c0 FROM pangya.pangya_item_warehouse WHERE [UID] = @IDUSER AND typeid = 436207832), 0)
        
