CREATE PROCEDURE [pangya].[ProcGachaJoga]
	-- Add the parameters for the stored procedure here
	@IDUSER INT,
	@INDEX_IN INT
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	DECLARE @MSGID INT = 0
	DECLARE @COIN INT,
			@QNTD_COIN INT,
			@COIN_ID INT
	
	SET @coin = 436207832
    
	SET @COIN_ID = (SELECT TOP 1 item_id FROM pangya.pangya_item_warehouse WHERE [UID] = @IDUSER AND typeid = @COIN)
    
    IF @COIN_ID IS NOT NULL AND @COIN_ID > 0
	BEGIN
		SET @qntd_coin = (SELECT TOP 1 c0 FROM pangya.pangya_item_warehouse WHERE [UID] = @IDUSER AND item_id = @COIN_ID)

		IF @qntd_coin IS NOT NULL AND @qntd_coin > 0
		BEGIN
			-- INICIO DE REMOVE COIN
			UPDATE pangya.pangya_item_warehouse SET c0 = c0 - 1 WHERE [UID] = @IDUSER AND item_id = @COIN_ID

			IF (SELECT TOP 1 c0 FROM pangya.pangya_item_warehouse WHERE [UID] = @IDUSER AND item_id = @COIN_ID) <= 0
			BEGIN
				UPDATE pangya.pangya_item_warehouse SET valid = 0, c0 = 0 WHERE [UID] = @IDUSER AND item_id = @COIN_ID
			END

			SELECT @COIN_ID AS ITEM_ID
			-- FIM DE REMOVE COIN
        
			IF (SELECT tipo FROM pangya.pangya_gacha_items WHERE [index] = @INDEX_IN) = 1
			BEGIN
				INSERT INTO pangya.pangya_gacha_user_won(gacha_num, uid, item_name, item_typeid) 
					SELECT a.numero, @IDUSER, b.nome, b.typeid 
					FROM pangya.pangya_gacha a, pangya.pangya_gacha_items b 
					WHERE a.numero = b.gacha_num AND b.[index] = @INDEX_IN
			END
        
			INSERT INTO pangya.pangya_gift_table(UID, FROMID, MESSAGE, GIFTDATE, LIDA_YN, VALID)
						VALUES(@IDUSER, N'@SUPERSS', N'Gacha Item', getdate(), 0, 1)

			--#SELECT MAX(MSG_ID) INTO MSGID FROM pangya_gift_table;
			SET @MSGID = @@identity;
        
			DECLARE @ITEM_TYPEID INT,
					@ITEM_QNTD INT

			SET @item_typeid = (SELECT b.typeid FROM pangya.pangya_gacha a, pangya.pangya_gacha_items b WHERE a.numero = b.gacha_num AND b.[index] = @INDEX_IN)
			SET @item_qntd = (SELECT b.qntd FROM pangya.pangya_gacha a, pangya.pangya_gacha_items b WHERE a.numero = b.gacha_num AND b.[index] = @INDEX_IN)
        
			exec pangya.ProcInsertItemNoEmail 0, @IDUSER, @MSGID, -1, @item_typeid, 0, @item_qntd, 0
        
			UPDATE pangya.pangya_gacha_user_key SET att_flag = 1 WHERE [UID] = @IDUSER
		END	-- COIN QNT
 
	END	-- COIN ID
	ELSE
		SELECT 0 as ITEM_ID;
END
