-- =============================================
CREATE PROCEDURE [pangya].[ProcGetAllItemByPlayer] 
	-- Add the parameters for the stored procedure here
	@IDUSER INT
AS
BEGIN
	
	IF @IDUSER > 0
	BEGIN

		SELECT
			a.typeid,
			a.[type]
		FROM
		(
			-- Select Caddie item
			SELECT typeid, 0 as [type] FROM pangya.pangya_caddie_information WHERE [UID] = @IDUSER AND Valid = 1 AND typeid > 0
			GROUP BY typeid 
			--ORDER BY typeid
			UNION ALL
			-- Select Mascot item
			SELECT typeid, 1 as [type] FROM pangya.pangya_mascot_info WHERE [UID] = @IDUSER AND Valid = 1 AND
				(Tipo != 1 OR [Period] = 0 OR EndDate = RegDate OR DATEDIFF(SECOND, getdate(), EndDate) > 0)
				AND typeid > 0
			GROUP BY typeid 
			--ORDER BY typeid
			UNION ALL
			-- Select Warehouse item
			SELECT typeid, 2 as [type] FROM pangya.pangya_item_warehouse WHERE [UID] = @IDUSER AND valid = 1 AND 
				Gift_Flag = 0 AND (flag = 96 OR applytime = enddate OR typeid = 0x1A000042/*TICKET REPORT*/ OR enddate > CURRENT_TIMESTAMP)
				AND typeid > 0
			GROUP BY typeid
			--ORDER BY typeid
			UNION ALL
			-- Dolphin Locker
			SELECT
				a.typeid, 3 as [type]
			FROM 
				pangya.pangya_item_warehouse a
				INNER JOIN pangya.pangya_dolfini_locker_item c
				ON a.item_id = c.item_id
			WHERE a.[uid] = @IDUSER AND c.flag = 1 AND a.typeid > 0
			GROUP BY a.typeid
			--ORDER BY a.typeid
			UNION ALL
			-- Mail Box
			SELECT
				b.item_typeid as typeid, 4 as [type]
			FROM
				pangya.pangya_gift_table a
				INNER JOIN
				pangya.pangya_item_mail b
				ON a.Msg_ID = b.Msg_ID
			WHERE
				a.[UID] = @IDUSER AND a.valid = 1 AND b.valid = 1
				AND b.item_typeid > 0
			GROUP BY b.item_typeid
		) a
		ORDER BY a.[type], a.typeid
	END
END
