	-- Add the parameters for the stored procedure here
	@PLAYER_UID INT
AS
BEGIN
	
	IF @PLAYER_UID > 0
	BEGIN

		DECLARE @OTHER_GUILD INT = (
			SELECT
				a.Guild_UID
			FROM
				pangya.account a
				INNER JOIN
				pangya.pangya_guild b
				ON a.Guild_UID = b.GUILD_UID
			WHERE a.[UID] = @PLAYER_UID AND 
				(b.GUILD_STATE NOT IN(4, 5) OR b.GUILD_CLOSURE_DATE IS NULL OR getdate() < b.GUILD_CLOSURE_DATE)
