CREATE PROCEDURE [pangya].[ProcGetGuildInfo]  
   @IDUSER int,
   @opt int
AS 
BEGIN

	IF @IDUSER > 0
	BEGIN

		SELECT 
			a.GUILD_UID, 
			a.GUILD_NAME, 
			(SELECT count_big(*) FROM pangya.pangya_guild_member WHERE GUILD_UID = a.GUILD_UID AND MEMBER_STATE_FLAG < 9) AS GUILD_NUM_MEMBER, 
			IIF(b.MEMBER_STATE_FLAG < 9, a.GUILD_MARK_IMG, N'') AS GUILD_MARK_IMG,
			IIF(b.MEMBER_STATE_FLAG < 9, a.GUILD_MARK_IMG_IDX, 0) AS GUILD_MARK_IMG_IDX,
			a.GUILD_NOTICE, 
			a.GUILD_INFO,
			IIF(@opt = 1, a.GUILD_POINT, b.GUILD_POINT) AS GUILD_POINT,
			IIF(@opt = 1, a.GUILD_PANG, b.GUILD_PANG) AS GUILD_PANG, 
			b.MEMBER_STATE_FLAG AS GUILD_MEMBER_IDENTITY, 
			a.GUILD_LEADER, 
			(SELECT account.NICK FROM pangya.account WHERE account.UID = a.GUILD_LEADER) AS GUILD_LEADER_NICK, 
			a.GUILD_REG_DATE
		FROM 
		pangya.pangya_guild AS a
		INNER JOIN 
		pangya.pangya_guild_member AS b
		ON a.GUILD_UID = b.GUILD_UID 
			AND b.MEMBER_STATE_FLAG < 9
			AND (a.GUILD_STATE NOT IN(4, 5) OR a.GUILD_CLOSURE_DATE IS NULL OR getdate() < a.GUILD_CLOSURE_DATE)
		INNER JOIN
		pangya.account AS c
		ON b.MEMBER_UID = c.[UID]
		WHERE 
			c.[UID] = @IDUSER    

	END

END
