AS
BEGIN
	
	IF @GUILD_UID > 0 AND @PAGE >= 0
	BEGIN

		DECLARE @OTHER_GUILD INT = (
			SELECT
				b.GUILD_UID
			FROM
				pangya.pangya_guild b
			WHERE b.GUILD_UID = @GUILD_UID
				AND (b.GUILD_STATE NOT IN(4, 5) OR b.GUILD_CLOSURE_DATE IS NULL OR getdate() < b.GUILD_CLOSURE_DATE)
		)

