			FROM
				pangya.pangya_guild_match a
			WHERE a.GUILD_1_UID = @GUILD_UID OR a.guild_2_uid = @GUILD_UID)

			SELECT
				@LINHAS AS LINHAS,
				a.GUILD_1_UID,
				(SELECT GUILD_NAME FROM pangya.pangya_guild WHERE GUILD_UID = a.GUILD_1_UID) AS GUILD_1_NAME,
