				a.GUILD_1_POINT,
				a.GUILD_1_PANG,
				a.GUILD_2_UID,
				(SELECT GUILD_NAME FROM pangya.pangya_guild WHERE GUILD_UID = a.GUILD_2_UID) AS GUILD_2_NAME,
				(SELECT GUILD_MARK_IMG_IDX FROM pangya.pangya_guild WHERE GUILD_UID = a.GUILD_2_UID) AS GUILD_2_MARK_INDEX,
