			WHERE a.GUILD_1_UID = @GUILD_UID OR a.guild_2_uid = @GUILD_UID
			ORDER BY a.reg_date DESC
			OFFSET (@PAGE * 13) ROWS
			FETCH NEXT 13 ROWS ONLY

