CREATE PROCEDURE [pangya].[ProcGetGuildRanking]
	-- Add the parameters for the stored procedure here
	@PAGE INT,
	@SEARCH NVARCHAR(100)
AS
BEGIN
	
	IF @PAGE >= 0
	BEGIN

		DECLARE @LINHAS BIGINT = 0,
			    @REG_DATE NVARCHAR(10)

		-- Total de linhas da consulta
		SELECT @LINHAS = COUNT_BIG(a.GUILD_UID), @REG_DATE = CAST(MAX(b.REG_DATE) AS NVARCHAR(10))
		FROM 
			pangya.pangya_guild a
			INNER JOIN
			pangya.pangya_guild_ranking b
			ON a.GUILD_UID = b.GUILD_UID
		WHERE 
			a.GUILD_NAME LIKE N'%' + @SEARCH + N'%'
			AND (a.GUILD_STATE NOT IN(4, 5) OR a.GUILD_CLOSURE_DATE IS NULL OR getdate() < a.GUILD_CLOSURE_DATE)

		SELECT 
			@LINHAS as LINHAS,
			@REG_DATE as REG_DATE,
			a.[RANK],
			a.GUILD_UID,
			a.GUILD_NAME,
			a.GUILD_WIN,
			a.GUILD_LOSE,
			a.GUILD_DRAW,
			a.GUILD_POINT,
			a.GUILD_PANG,
			a.LAST_RANK,
			a.GUILD_MARK_IMG_IDX
		FROM
			(
				SELECT	
					b.[RANK],
					b.LAST_RANK,
					a.GUILD_UID,
					a.GUILD_NAME,
					a.GUILD_WIN,
					a.GUILD_LOSE,
					a.GUILD_DRAW,
					a.GUILD_POINT,
					a.GUILD_PANG,
					a.GUILD_MARK_IMG_IDX,
					b.REG_DATE
				FROM 
					pangya.pangya_guild a
					INNER JOIN
					pangya.pangya_guild_ranking b
					ON a.GUILD_UID = b.GUILD_UID
				WHERE 
					a.GUILD_NAME LIKE N'%' + @SEARCH + N'%'
					AND (a.GUILD_STATE NOT IN(4, 5) OR a.GUILD_CLOSURE_DATE IS NULL OR getdate() < a.GUILD_CLOSURE_DATE)
			) a
		ORDER BY a.[RANK]
			OFFSET (@PAGE * 10) ROWS
			FETCH NEXT 10 ROWS ONLY

	END
END
