CREATE PROCEDURE [pangya].[ProcGetGuildRSSInfo] 
	-- Add the parameters for the stored procedure here
	@PLAYER_UID INT
AS
BEGIN
	
	IF @PLAYER_UID > 0
	BEGIN

		DECLARE @OTHER_GUILD INT = (
			SELECT
				a.Guild_UID
			FROM
				pangya.account a
				INNER JOIN
				pangya.pangya_guild b
				ON a.Guild_UID = b.GUILD_UID
			WHERE a.[UID] = @PLAYER_UID AND 
				(b.GUILD_STATE NOT IN(4, 5) OR b.GUILD_CLOSURE_DATE IS NULL OR getdate() < b.GUILD_CLOSURE_DATE)
		)

		-- Player está em uma guild válida
		IF @OTHER_GUILD IS NOT NULL
		BEGIN

			DECLARE @GUILD_UID INT = 0

			SET @GUILD_UID = (SELECT Guild_UID FROM pangya.account WHERE [UID] = @PLAYER_UID)

			IF @GUILD_UID IS NOT NULL AND @GUILD_UID > 0
			BEGIN
				
				DECLARE @RSS_VER BIGINT = 

				-- RSS Version is last sequence id Notice
				(SELECT TOP(1)
					b.SEQ AS RSS_VER
				FROM
					pangya.pangya_guild a
					INNER JOIN
					pangya.pangya_guild_notice b
					ON a.GUILD_UID = b.GUILD_UID
				WHERE a.GUILD_UID = @GUILD_UID AND b.[STATE] = 1
				ORDER BY b.REG_DATE DESC)

				-- Last 5 Notice
				SELECT 
					@RSS_VER as RSS_VER,
					b.TITLE,
					b.[TEXT],
					(SELECT nick FROM pangya.account WHERE [uid] = b.OWNER_UID) AS NICKNAME,
					CAST(b.REG_DATE AS NVARCHAR(19)) AS REG_DATE
				FROM 
					pangya.pangya_guild a
					INNER JOIN
					pangya.pangya_guild_notice b
					ON a.GUILD_UID = b.GUILD_UID
				WHERE a.GUILD_UID = @GUILD_UID AND b.[STATE] = 1
				ORDER BY b.REG_DATE DESC
				OFFSET 0 ROWS
				FETCH NEXT 5 ROWS ONLY

			END
			ELSE
				SELECT 0 AS RSS_VER

		END
		ELSE
			SELECT 0 AS RSS_VER

	END
	ELSE
		SELECT 0 AS RSS_VER
END
