	BEGIN
		
		SELECT 
				a.[uid], 
				a.id, 
				a.nick, 
				c.[level], 
				a.idstate, 
				a.capability as cap, 
				c.pang, 
				c.cookie, 
				d.channel, 
				d.lobby, 
				d.room, 
				d.place, 
				a.logonCount, 
				e.[counter],
				a.userip,
				CAST(a.LastLogonTime AS NVARCHAR(16)) AS LastLogonTime, 
				a.logon, 
				a.game_server_id, 
				b.[name] 
		FROM pangya.account a, pangya.user_info c, pangya.pangya_server_list b, pangya.pangya_player_location d, pangya.pangya_attendance_reward e
		WHERE a.game_server_id = b.[uid]  AND a.[uid] = c.[uid] AND a.[uid] = d.[uid] AND a.[UID] = e.[UID]

	END
	ELSE IF @TYPE = 1 -- Todas as contas que não fizeram login
	BEGIN

		SELECT 
				a.[uid], 
				a.id, 
				a.nick, 
				c.[level], 
				a.idstate, 
				a.capability as cap, 
				c.pang, 
				c.cookie, 
				d.channel, 
				d.lobby, 
				d.room, 
				d.place, 
				a.logonCount,
				e.[counter],
				a.userip,
				CAST(a.LastLogonTime AS NVARCHAR(16)) AS LastLogonTime, 
				a.logon, 
				a.game_server_id, 
				'unknown' as [name] 
		FROM pangya.account a, pangya.user_info c, pangya.pangya_player_location d, pangya.pangya_attendance_reward e
		WHERE  a.[uid] = c.[uid] AND a.[uid] = d.[uid] AND a.[UID] = e.[UID] AND (a.first_login = 0 OR a.first_set = 0)

	END
	ELSE IF @TYPE = 2 -- Todas as contas online no momento
	BEGIN
		
		SELECT 
				a.[uid], 
				a.id, 
				a.nick, 
				c.[level], 
				a.idstate, 
				a.capability as cap, 
				c.pang, c.cookie, 
				d.channel, 
				d.lobby, 
				d.room, 
				d.place, 
				a.logonCount, 
				e.[counter],
				a.userip,
				CAST(a.LastLogonTime AS NVARCHAR(16)) AS LastLogonTime,
				a.logon, 
				a.game_server_id, 
				b.[name] 
		FROM pangya.account a, pangya.user_info c, pangya.pangya_server_list b, pangya.pangya_player_location d, pangya.pangya_attendance_reward e
		WHERE a.logon = 1 AND a.game_server_id = b.[uid] AND a.[uid] = c.[uid] AND a.[uid] = d.[uid] AND a.[UID] = e.[UID]

	END
	ELSE IF @TYPE = 4 -- Todas as contas banidas
	BEGIN
		
		SELECT 
				a.[uid], 
				a.id, 
				a.nick, 
				c.[level], 
				a.idstate, 
				a.capability as cap, 
				c.pang, 
				c.cookie, 
				d.channel, 
				d.lobby, 
				d.room, 
				d.place, 
				a.logonCount,
				e.[counter],
				a.userip,
				CAST(a.LastLogonTime AS NVARCHAR(16)) AS LastLogonTime, 
				a.logon, 
				a.game_server_id, 
				ISNULL(b.[Name], 'unknown') as [name] 
		FROM 
			pangya.account a
			INNER JOIN
			pangya.user_info c
			ON a.[uid] = c.[uid]
			INNER JOIN
			pangya.pangya_player_location d
			ON a.[uid] = d.[uid]
			INNER JOIN
			pangya.pangya_attendance_reward e
			ON a.[UID] = e.[UID]
			LEFT OUTER JOIN
			pangya_server_list b
			ON b.[UID] = a.game_server_id
		WHERE 
			(a.IDState = 1 AND ISNULL(DATEADD(MINUTE, a.BlockTime, a.BlockRegDate), getdate()) > getdate() OR a.IDState > 1)
	END

END
