CREATE PROCEDURE [pangya].[ProcGetListGachaSystemPlayerItens]
	-- Add the parameters for the stored procedure here
	@UID INT,
	@PAGE INT,
	@TYPE INT,
	@TYPE_ITEM INT
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	IF @UID > 0 AND @PAGE >= 0
	BEGIN

		DECLARE @LINHAS BIGINT = 

		-- Total de linhas da consulta
		(SELECT 
			COUNT_BIG(a.[index]) AS LINHAS
		FROM 
			pangya.pangya_gacha_jp_player_win a
			INNER JOIN
			pangya.pangya_gacha_jp_all_item_list b
			ON a.typeid = b.typeid
		WHERE a.[UID] = @UID AND a.valid = 1 AND a.send_mail = 0
			AND (@TYPE = 1 -- Type 1 retorna todos os itens
			OR (@TYPE = 2 AND a.rarity_type = 0 AND ((@TYPE_ITEM = 100 AND b.char_type > 10/*Spika*/) OR @TYPE_ITEM = b.char_type)) -- Type 2 só retorna os raros, com o seu char_type
			OR (@TYPE = 3 AND a.rarity_type != 0 AND ((@TYPE_ITEM = 1 AND b.char_type = 11/*Aztec*/) OR (@TYPE_ITEM = 2 AND b.char_type != 11/*Aztec*/))))) -- Itens e Aztec

		SELECT 
			@LINHAS as LINHAS,
			a.[index] AS ID,
			a.typeid AS TYPEID,
			a.qnty AS QNTD,
			b.[name] AS [NAME]
		FROM 
			pangya.pangya_gacha_jp_player_win a
			INNER JOIN
			pangya.pangya_gacha_jp_all_item_list b
			ON a.typeid = b.typeid
		WHERE a.[UID] = @UID AND a.valid = 1 AND a.send_mail = 0
			AND (@TYPE = 1 -- Type 1 retorna todos os itens
			OR (@TYPE = 2 AND a.rarity_type = 0 AND ((@TYPE_ITEM = 100 AND b.char_type > 10/*Spika*/) OR @TYPE_ITEM = b.char_type)) -- Type 2 só retorna os raros, com o seu char_type
			OR (@TYPE = 3 AND a.rarity_type != 0 AND ((@TYPE_ITEM = 1 AND b.char_type = 11/*Aztec*/) OR (@TYPE_ITEM = 2 AND b.char_type != 11/*Aztec*/)))) -- Itens e Aztec
		ORDER BY b.REG_DATE DESC
		OFFSET (@PAGE * 50) ROWS
		FETCH NEXT 50 ROWS ONLY
	END
END
