CREATE PROCEDURE [pangya].[ProcGetListGuild]
	-- Add the parameters for the stored procedure here
	@PAGE INT,
	@CATEGORY INT,
	@SEARCH NVARCHAR(22),
	@SORT INT
AS
BEGIN
	
	IF @PAGE >= 0
	BEGIN

		DECLARE @LINHAS BIGINT = 0

		IF @CATEGORY = 0	-- GUILD NAME
		BEGIN

			SET @LINHAS = 

			-- Total de linhas da consulta
			(SELECT COUNT_BIG(a.GUILD_UID) AS LINHAS 
			FROM 
			pangya.pangya_guild a
			INNER JOIN
			pangya.account b
			ON a.GUILD_LEADER = b.[uid]
			WHERE 
				a.GUILD_NAME LIKE N'%' + @SEARCH + N'%'
				AND (a.GUILD_STATE NOT IN(4, 5) OR a.GUILD_CLOSURE_DATE IS NULL OR getdate() < a.GUILD_CLOSURE_DATE))

			SELECT 
				@LINHAS as LINHAS,
				a.GUILD_UID,
				a.GUILD_NAME,
				a.GUILD_MASTER,
				a.MEMBERS,
				a.GUILD_CONDITION_LEVEL,
				a.GUILD_STATE,
				a.GUILD_FLAG,
				a.GUILD_MARK_IMG_IDX,
				CAST(a.GUILD_REG_DATE as NVARCHAR(10)) as GUILD_REG_DATE
			FROM
				(
					SELECT	a.*,
							(SELECT COUNT_BIG(GUILD_UID) FROM pangya.pangya_guild_member WHERE GUILD_UID = a.GUILD_UID AND MEMBER_STATE_FLAG < 9) as MEMBERS,
							b.NICK as GUILD_MASTER
					FROM 
					pangya.pangya_guild a
					INNER JOIN
					pangya.account b
					ON a.GUILD_LEADER = b.[uid]
					WHERE 
						a.GUILD_NAME LIKE N'%' + @SEARCH + N'%'
						AND (a.GUILD_STATE NOT IN(4, 5) OR a.GUILD_CLOSURE_DATE IS NULL OR getdate() < a.GUILD_CLOSURE_DATE)
				) a
			ORDER BY
				CASE WHEN @SORT = 0 THEN a.GUILD_NAME ELSE null END,
				CASE WHEN @SORT = 1 THEN a.GUILD_MASTER ELSE null END,
				CASE WHEN @SORT = 2 THEN a.MEMBERS ELSE null END DESC,
				CASE WHEN @SORT = 3 THEN a.GUILD_CONDITION_LEVEL ELSE null END,
				CASE WHEN @SORT = 4 THEN a.GUILD_STATE ELSE null END,
				CASE WHEN @SORT = 5 THEN a.GUILD_FLAG ELSE null END,
				CASE WHEN @SORT = 6 THEN CAST(a.GUILD_REG_DATE as sql_variant) ELSE CAST(a.GUILD_REG_DATE as sql_variant) END DESC
				OFFSET (@PAGE * 12) ROWS
				FETCH NEXT 12 ROWS ONLY

		END
		ELSE	-- GUILD MASTER NICKNAME
		BEGIN

			SET @LINHAS = 

			-- Total de linhas da consulta
			(SELECT COUNT_BIG(a.GUILD_UID) AS LINHAS 
			FROM 
			pangya.pangya_guild a
			INNER JOIN
			pangya.account b
			ON a.GUILD_LEADER = b.[uid]
			WHERE 
				b.NICK LIKE N'%' + @SEARCH + N'%'
				AND (a.GUILD_STATE NOT IN(4, 5) OR a.GUILD_CLOSURE_DATE IS NULL OR getdate() < a.GUILD_CLOSURE_DATE))
			
			SELECT 
				@LINHAS as LINHAS,
				a.GUILD_UID,
				a.GUILD_NAME,
				a.GUILD_MASTER,
				a.MEMBERS,
				a.GUILD_CONDITION_LEVEL,
				a.GUILD_STATE,
				a.GUILD_FLAG,
				a.GUILD_MARK_IMG_IDX,
				CAST(a.GUILD_REG_DATE as NVARCHAR(10)) as GUILD_REG_DATE
			FROM
				(
					SELECT	a.*,
							(SELECT COUNT_BIG(GUILD_UID) FROM pangya.pangya_guild_member WHERE GUILD_UID = a.GUILD_UID AND MEMBER_STATE_FLAG < 9) as MEMBERS,
							b.NICK as GUILD_MASTER
					FROM 
					pangya.pangya_guild a
					INNER JOIN
					pangya.account b
					ON a.GUILD_LEADER = b.[uid]
					WHERE 
						b.NICK LIKE N'%' + @SEARCH + N'%'
						AND (a.GUILD_STATE NOT IN(4, 5) OR a.GUILD_CLOSURE_DATE IS NULL OR getdate() < a.GUILD_CLOSURE_DATE)
				) a
			ORDER BY
				CASE WHEN @SORT = 0 THEN a.GUILD_NAME ELSE null END,
				CASE WHEN @SORT = 1 THEN a.GUILD_MASTER ELSE null END,
				CASE WHEN @SORT = 2 THEN a.MEMBERS ELSE null END DESC,
				CASE WHEN @SORT = 3 THEN a.GUILD_CONDITION_LEVEL ELSE null END,
				CASE WHEN @SORT = 4 THEN a.GUILD_STATE ELSE null END,
				CASE WHEN @SORT = 5 THEN a.GUILD_FLAG ELSE null END,
				CASE WHEN @SORT = 6 THEN CAST(a.GUILD_REG_DATE as sql_variant) ELSE CAST(a.GUILD_REG_DATE as sql_variant) END DESC
				OFFSET (@PAGE * 12) ROWS
				FETCH NEXT 12 ROWS ONLY

		END	-- ELSE IFF CATEGORY

	END -- CHECK PAGE
	

END
