	-- Add the parameters for the stored procedure here
	@PAGE INT,
	@CATEGORY INT,
	@SEARCH NVARCHAR(22),
	@SORT INT
AS
BEGIN
	
	IF @PAGE >= 0
	BEGIN

		DECLARE @LINHAS BIGINT = 

		-- Total de linhas da consulta
		(SELECT 
			COUNT_BIG(a.SEQ) AS LINHAS
		FROM 
			pangya.pangya_guild_bbs a
		WHERE 
			[STATE] = 1 AND (@CATEGORY = 10 OR a.[TYPE] = @CATEGORY)
			AND a.TITLE LIKE N'%' + @SEARCH + N'%')

		SELECT
			@LINHAS as LINHAS,
			a.SEQ,
			a.[TYPE],
			a.TITLE,
			a.RES_COUNT,
			a.NICKNAME,
			CAST(a.REG_DATE AS NVARCHAR(10)) AS REG_DATE
		FROM
			(
				SELECT 
					a.SEQ,
					a.[TYPE],
					a.TITLE,
					(SELECT COUNT_BIG(SEQ) FROM pangya.pangya_guild_bbs_res WHERE BBS_SEQ = a.SEQ AND [STATE] = 1) AS RES_COUNT,
					(SELECT nick FROM pangya.account WHERE [UID] = a.OWNER_UID) AS NICKNAME,
					a.REG_DATE
				FROM 
					pangya.pangya_guild_bbs a
				WHERE 
					[STATE] = 1 AND (@CATEGORY = 10 OR a.[TYPE] = @CATEGORY)
					AND a.TITLE LIKE N'%' + @SEARCH + N'%'
			) a
		ORDER BY
			CASE WHEN @SORT = 0 THEN (SELECT MAX(REG_DATE) FROM pangya.pangya_guild_bbs_res WHERE BBS_SEQ = a.SEQ AND [STATE] = 1) ELSE null END DESC,
			CASE WHEN @SORT = 6 THEN a.REG_DATE ELSE a.REG_DATE END DESC,
			a.SEQ DESC
			OFFSET (@PAGE * 14) ROWS
			FETCH NEXT 14 ROWS ONLY

	END -- CHECK PAGE
END
