CREATE PROCEDURE [pangya].[ProcGetListGuildWeb]
	@OPT INT
AS
BEGIN
	
	SELECT
		a.GUILD_INTRO_IMG,
		a.GUILD_UID,
		a.GUILD_NAME,
		a.GUILD_MARK_IMG_IDX,
		a.GUILD_NEW_MARK_IDX,
		ISNULL((SELECT NICK FROM pangya.account WHERE [UID] = a.GUILD_LEADER), '') AS GUILD_MASTER,
		ISNULL((SELECT NICK FROM pangya.account WHERE [UID] = a.GUILD_SUB_MASTER), '') AS GUILD_SUB_MASTER,
		(SELECT COUNT_BIG(GUILD_UID) FROM pangya.pangya_guild_member WHERE GUILD_UID = a.GUILD_UID AND MEMBER_STATE_FLAG < 9) as MEMBERS,
