		a.GUILD_STATE,
		a.GUILD_FLAG,
		a.GUILD_PERMITION_JOIN,
		a.GUILD_POINT,
		a.GUILD_PANG,
		a.GUILD_INFO,
		a.GUILD_NOTICE,
		CAST(a.GUILD_REG_DATE AS NVARCHAR(19)) AS GUILD_REG_DATE,
		CAST(a.GUILD_ACCEPT_DATE AS NVARCHAR(19)) AS GUILD_ACCEPT_DATE,
		CAST(a.GUILD_CLOSURE_DATE AS NVARCHAR(19)) AS GUILD_CLOSURE_DATE
