AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	
	IF @SERVER_UID > 0
	BEGIN
		DECLARE @AUTH_KEY NVARCHAR(16)

		SELECT @AUTH_KEY = upper(convert(varchar(32), hashbytes('md5', convert(varchar(32), rand(), 2)), 2));

		IF NOT EXISTS(SELECT * FROM pangya.pangya_auth_key WHERE SERVER_UID = @SERVER_UID)
