			UPDATE pangya.pangya_auth_key SET [KEY] = @AUTH_KEY, valid = 1
			WHERE [SERVER_UID] = @SERVER_UID
		END

		SELECT @AUTH_KEY AS NEW_AUTH_SERVER
	END
