CREATE PROCEDURE [pangya].[ProcGetNotice]
	-- Add the parameters for the stored procedure here
	@ID INT
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	DECLARE @NOTICE_ID INT = 0
	DECLARE @REFRESH_TIME INT = 0

	--Get Notice
    SET @NOTICE_ID = (SELECT arg1 FROM pangya.pangya_command WHERE /*command_id = 0 AND*/ idx = @ID)
    
    UPDATE pangya_notice_list SET replayCount = replayCount - 1 WHERE notice_id = @NOTICE_ID
    
    IF (SELECT replayCount FROM pangya_notice_list WHERE notice_id = @NOTICE_ID) <= 0
		UPDATE pangya_command SET valid = 0 WHERE /*command_id = 0 AND*/ idx = @ID
	ELSE
	BEGIN
		SET @REFRESH_TIME = (SELECT refreshTime FROM pangya_notice_list WHERE notice_id = @NOTICE_ID)
        
		UPDATE pangya_command SET reserveDate = dateadd(MINUTE, @REFRESH_TIME, getdate()) WHERE /*command_id = 0 AND*/ idx = @ID
    END
    
	SELECT [message] FROM pangya_notice_list WHERE notice_id = @NOTICE_ID
END
