CREATE PROCEDURE [pangya].[ProcGetShutdownServer]
	-- Add the parameters for the stored procedure here
	@ID INT
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	--Get Shutdown Server
	DECLARE @SHUTDOWN_ID INT = 0
	DECLARE @REFRESH_TIME INT = 0

    SET @SHUTDOWN_ID = (SELECT arg1 FROM pangya_command WHERE command_id = 3 AND idx = @ID)
    
    UPDATE pangya_shutdown_list SET replayCount = replayCount - 1 WHERE id = @SHUTDOWN_ID
    
    IF (SELECT replayCount FROM pangya_shutdown_list WHERE id = @SHUTDOWN_ID) <= 0
		UPDATE pangya_command SET valid = 0 WHERE command_id = 3 AND idx = @ID
	ELSE
	BEGIN
		SET @REFRESH_TIME = (SELECT refreshTime FROM pangya_shutdown_list WHERE id = @SHUTDOWN_ID)
        
		UPDATE pangya_command SET reserveDate = dateadd(MINUTE, @REFRESH_TIME, getdate()) WHERE command_id = 3 AND idx = @ID
    END
    
	SELECT 
        datediff(SECOND, getdate(), date_shutdown) as sec
    FROM pangya_shutdown_list WHERE id = @SHUTDOWN_ID
END
