CREATE PROCEDURE [pangya].[ProcGetTicketReportDados]  
   @ticket_report_id int
AS 
BEGIN

	IF @ticket_report_id > 0
	BEGIN

		SELECT 
			A.[idx],
			A.tipo, 
			A.trofel_typeid,
			A.reg_date,
			B.player_uid, 
			B.player_score, 
			B.player_medalha, 
			B.player_trofel, 
			B.player_pang, 
			B.player_bonus_pang, 
			B.player_exp, 
			B.player_mascot_typeid, 
			B.player_state, 
			B.flag_item_pang, 
			B.flag_premium_user, 
			D.[level], 
			C.ID, 
			C.NICK, 
			C.Guild_UID, 
			ISNULL(E.GUILD_MARK_IMG, 'guild_mark') as GUILD_MARK_IMG,
			ISNULL(E.GUILD_MARK_IMG_IDX, 0) AS GUILD_MARK_IMG_IDX,
			B.finish_date
		FROM 
			pangya.pangya_ticket_report A
			INNER JOIN 
			pangya.pangya_ticket_report_dados B
			ON A.[idx] = B.report_id
			INNER JOIN
			pangya.account C
			ON B.player_uid = C.[UID]
			INNER JOIN
			pangya.user_info D
			ON C.[UID] = D.[UID]
			LEFT OUTER JOIN
			pangya.pangya_guild E
			ON C.Guild_UID = E.GUILD_UID
