CREATE PROCEDURE [pangya].[ProcGuildAcceptMark]
	-- Add the parameters for the stored procedure here
	@GUILD_UID INT
AS
BEGIN

	DECLARE @RET INT = -1
	
	IF @GUILD_UID > 0
	BEGIN
		
		DECLARE @GUILD_MASTER INT = (SELECT GUILD_LEADER FROM pangya.pangya_guild WHERE [GUILD_UID] = @GUILD_UID)
		DECLARE @GUILD_NEW_MARK INT = (SELECT GUILD_NEW_MARK_IDX FROM pangya.pangya_guild WHERE [GUILD_UID] = @GUILD_UID)

		IF @GUILD_NEW_MARK IS NOT NULL AND @GUILD_NEW_MARK > 0
		BEGIN
			
			IF @GUILD_MASTER IS NOT NULL
			BEGIN
			
				UPDATE
					pangya.pangya_guild
						SET GUILD_STATE = IIF(GUILD_MARK_IMG_IDX = 0 AND GUILD_STATE = 0/*pending*/, 1/*Now Open*/, GUILD_STATE),
							GUILD_ACCEPT_DATE = IIF(GUILD_MARK_IMG_IDX = 0 AND GUILD_STATE = 0/*pending*/, getdate()/*Accept Guild*/, GUILD_ACCEPT_DATE),
							GUILD_MARK_IMG = CONCAT(GUILD_UID, '_', @GUILD_NEW_MARK),	-- Guild Mark Img, a string montada já
							GUILD_MARK_IMG_IDX = @GUILD_NEW_MARK,
							GUILD_NEW_MARK_IDX = 0
				WHERE [GUILD_UID] = @GUILD_UID

				-- Insert Activity Player
				INSERT INTO pangya.pangya_guild_atividade_player(GUILD_UID, [UID], [FLAG])
				VALUES(@GUILD_UID, @GUILD_MASTER, 15)

				-- Sucesso
				SET @RET = 1
			
			END

		END
		ELSE
			SET @RET = -2	-- Não tem um new mark para ser aceito
	END

	SELECT @RET AS RET
END
