CREATE PROCEDURE [pangya].[ProcGuildChangeAuthorityMember]
	-- Add the parameters for the stored procedure here
	@GUILD_UID INT,
	@MEMBER_UID INT,
	@MEMBER_AUTHORITY INT
AS
BEGIN
	-- Ret value
	DECLARE @RET INT = -1
	
	IF @GUILD_UID > 0 AND @MEMBER_UID > 0 AND @MEMBER_AUTHORITY >= 0
	BEGIN

		DECLARE @FLAG INT = (SELECT MEMBER_STATE_FLAG FROM pangya.pangya_guild_member WHERE GUILD_UID = @GUILD_UID AND MEMBER_UID = @MEMBER_UID)
		
		IF @FLAG IS NOT NULL
		BEGIN

			IF @FLAG != @MEMBER_AUTHORITY
			BEGIN

				DECLARE @OLD_SUB_MASTER INT = (SELECT GUILD_SUB_MASTER FROM pangya.pangya_guild WHERE [GUILD_UID] = @GUILD_UID)
			
				IF @OLD_SUB_MASTER IS NOT NULL
				BEGIN
					
					IF @MEMBER_AUTHORITY = 2 -- SUB MASTER
					BEGIN

						-- Update Member Authority
						UPDATE
							pangya.pangya_guild_member
								SET MEMBER_STATE_FLAG = @MEMBER_AUTHORITY
						WHERE 
							GUILD_UID = @GUILD_UID AND MEMBER_UID = @MEMBER_UID

						-- Update Guild Sub Master
						UPDATE
							pangya.pangya_guild
								SET GUILD_SUB_MASTER = @MEMBER_UID
						WHERE [GUILD_UID] = @GUILD_UID

						-- Activity member new Sub Master
						INSERT INTO pangya.pangya_guild_atividade_player(GUILD_UID, [UID], FLAG)
						VALUES(@GUILD_UID, @MEMBER_UID, 12/*Promovido a subMaster*/)

						-- Verifica se tinha um Sub Master para atualizar o state dele
						IF @OLD_SUB_MASTER > 0
						BEGIN
							
							UPDATE
								pangya.pangya_guild_member
									SET MEMBER_STATE_FLAG = 3 -- Member
							WHERE [GUILD_UID] = @GUILD_UID AND MEMBER_UID = @OLD_SUB_MASTER

							-- Activity old Sub Master to member
							INSERT INTO pangya.pangya_guild_atividade_player(GUILD_UID, [UID], [FLAG])
							VALUES(@GUILD_UID, @OLD_SUB_MASTER, 13/*Despromovido*/)
						END

					END
					ELSE	-- Membro
					BEGIN
						
						-- Update Member Authority
						UPDATE
							pangya.pangya_guild_member
								SET MEMBER_STATE_FLAG = @MEMBER_AUTHORITY
						WHERE 
							GUILD_UID = @GUILD_UID AND MEMBER_UID = @MEMBER_UID

						-- Update Guild Sub Master
						UPDATE
							pangya.pangya_guild
								SET GUILD_SUB_MASTER = 0	-- Nenhum Master
						WHERE [GUILD_UID] = @GUILD_UID

						-- Activity member new Sub Master
						INSERT INTO pangya.pangya_guild_atividade_player(GUILD_UID, [UID], FLAG)
						VALUES(@GUILD_UID, @MEMBER_UID, 13/*Despromovido*/)

					END

					SET @RET = 1	-- Trocou autoridade do membro com sucesso

				END

			END
			ELSE
				SET @RET = -2	-- Ele já é subMaster ou Membro
		END
	END

	SELECT @RET AS RET
END
GO
