CREATE PROCEDURE [pangya].[ProcGuildClosure] 
	-- Add the parameters for the stored procedure here
	@GUILD_UID INT,
	@MASTER_UID INT
AS
BEGIN
	
	DECLARE @RET INT = -1

	IF @GUILD_UID > 0 AND @MASTER_UID >= 0
	BEGIN

		IF (SELECT GUILD_UID FROM pangya.pangya_guild WHERE [GUILD_UID] = @GUILD_UID AND ((@MASTER_UID = 0 AND GUILD_STATE IN(0, 1, 2, 3)) OR (@MASTER_UID > 0 AND GUILD_STATE IN(0, 1, 2)))) IS NOT NULL
		BEGIN

			-- Dias que a guild vai ficar em estado de fechamento antes de fechar
			DECLARE @DIAS INT = 7

			IF @MASTER_UID = 0	-- GM Fechou a guild
			BEGIN

				DECLARE @GUILD_MASTER INT = (SELECT GUILD_LEADER FROM pangya.pangya_guild WHERE [GUILD_UID] = @GUILD_UID)
			
				IF @GUILD_MASTER IS NOT NULL
				BEGIN

					UPDATE
						pangya.pangya_guild
							SET GUILD_STATE = 5, -- GM FECHOU A GUILD
								GUILD_CLOSURE_DATE = DATEADD(DAY, @DIAS, getdate())
					WHERE [GUILD_UID] = @GUILD_UID

					-- Insert Activity Player
					INSERT INTO pangya.pangya_guild_atividade_player(GUILD_UID, [UID], [FLAG])
					VALUES(@GUILD_UID, @GUILD_MASTER, 17)

					-- Sucesso
					SET @RET = 1

				END

			END
			ELSE
			BEGIN
			
				UPDATE
					pangya.pangya_guild
						SET GUILD_STATE = 4,	-- Player fechou a guild
							GUILD_CLOSURE_DATE = DATEADD(DAY, @DIAS, getdate())
				WHERE [GUILD_UID] = @GUILD_UID AND GUILD_LEADER = @MASTER_UID

				-- Insert Activity Player
				INSERT INTO pangya.pangya_guild_atividade_player(GUILD_UID, [UID], [FLAG])
				VALUES(@GUILD_UID, @MASTER_UID, 9)

				-- Sucesso
				SET @RET = 1
					
			END
		END
		ELSE
			SET @RET = -2	-- A guild já está fechada

	END

	SELECT @RET AS RET
END
