CREATE PROCEDURE [pangya].[ProcGuildKickMember] 
	-- Add the parameters for the stored procedure here
	@OWNER_UID INT,	-- Quem fez a Ação
	@MEMBER_UID INT,
	@GUILD_UID INT
AS
BEGIN
	
	IF @GUILD_UID > 0 AND @MEMBER_UID > 0
	BEGIN

		DECLARE @FLAG INT = (SELECT MEMBER_STATE_FLAG FROM pangya.pangya_guild_member WHERE GUILD_UID = @GUILD_UID AND MEMBER_UID = @MEMBER_UID)

		IF @FLAG IS NOT NULL
		BEGIN

			IF @FLAG = 2 -- SUB MASTER
				UPDATE pangya.pangya_guild SET GUILD_SUB_MASTER = 0 WHERE GUILD_UID = @GUILD_UID

			IF @FLAG = 9
				SET @FLAG = 4 -- Rejeita pedido para entrar na guild
			ELSE
				SET @FLAG = 6 -- Kick Member (Expulsa)
		
			-- Delete from table members of guild
			DELETE FROM
				pangya.pangya_guild_member
			WHERE 
				GUILD_UID = @GUILD_UID AND MEMBER_UID = @MEMBER_UID

			-- Update Guild of player in table account
			UPDATE
				pangya.account
					SET Guild_UID = 0
			WHERE [UID] = @MEMBER_UID

			-- Insert Ativity of player in table guild atividade
			INSERT INTO pangya.pangya_guild_atividade_player(GUILD_UID, [UID], [FLAG])
			VALUES(@GUILD_UID, @MEMBER_UID, @FLAG)

			IF @FLAG = 6 -- Kick Member (Expulsa)
			BEGIN
				-- Cria log de update para o Game Server verificar e pedir para o Message Server Atualize os Membros do Club
				INSERT INTO pangya_guild_update_activity(GUILD_UID, OWNER_UPDATE, PLAYER_UID, TYPE_UPDATE) VALUES(@GUILD_UID, @OWNER_UID, @MEMBER_UID, 2/*Kick Member*/)
			END
		END

