CREATE PROCEDURE [pangya].[ProcGuildMoveMaster] 
	-- Add the parameters for the stored procedure here
	@GUILD_UID INT,
	@MEMBER_UID INT
AS
BEGIN

	DECLARE @RET INT = -1
	
	IF @GUILD_UID > 0 AND @MEMBER_UID > 0
	BEGIN

		-- Verifica se o membro que vai virar master da guild tem o level de Rookie E pra cima
		DECLARE @LEVEL INT = (SELECT [level] FROM pangya.user_info WHERE [UID] = @MEMBER_UID)

		IF @LEVEL IS NOT NULL AND @LEVEL > 0
		BEGIN

			DECLARE @OLD_MASTER INT = (SELECT GUILD_LEADER FROM pangya.pangya_guild WHERE [GUILD_UID] = @GUILD_UID)
			DECLARE @OLD_SUB_MASTER INT = (SELECT GUILD_SUB_MASTER FROM pangya.pangya_guild WHERE [GUILD_UID] = @GUILD_UID)

			IF @OLD_MASTER IS NOT NULL AND @OLD_SUB_MASTER IS NOT NULL
			BEGIN

				-- Update Master of Guild
				UPDATE
					pangya.pangya_guild
						SET GUILD_LEADER = @MEMBER_UID
					WHERE
						GUILD_UID = @GUILD_UID

				-- Update Member state flag to Master
				UPDATE
					pangya.pangya_guild_member
						SET MEMBER_STATE_FLAG = 1 -- Master
				WHERE
					GUILD_UID = @GUILD_UID AND MEMBER_UID = @MEMBER_UID

				-- Update Old Master to member
				UPDATE
					pangya.pangya_guild_member
						SET MEMBER_STATE_FLAG = 3 -- Member
				WHERE
					GUILD_UID = @GUILD_UID AND MEMBER_UID = @OLD_MASTER

				-- Verifica se o membro que virou Master era Sub Master e tira o Sub Master da Guild
				IF @OLD_SUB_MASTER = @MEMBER_UID
				BEGIN

					UPDATE
						pangya.pangya_guild
							SET GUILD_SUB_MASTER = 0	-- Zera, não tem mais sub master, só master já que o master virou membro comun
					WHERE [GUILD_UID] = @GUILD_UID

				END

				-- Insert Activity of New Master
				INSERT INTO pangya.pangya_guild_atividade_player(GUILD_UID, [UID], [FLAG])
				VALUES(@GUILD_UID, @MEMBER_UID, 11/*Promovido a master*/)

				-- Insert Activity of Old Master to member
				INSERT INTO pangya.pangya_guild_atividade_player(GUILD_UID, [UID], [FLAG])
				VALUES(@GUILD_UID, @OLD_MASTER, 5/*Promote*/)

				-- Passou o master
				INSERT INTO pangya.pangya_guild_atividade_player(GUILD_UID, [UID], [FLAG])
				VALUES(@GUILD_UID, @OLD_MASTER, 10/*Passou o master*/)

				-- Sucesso
				SET @RET = 1

			END

		END
		ELSE
			SET @RET = -2

	END

	SELECT @RET AS RET
