	@GUILD_UID INT,
	@MASTER_UID INT
AS
BEGIN

	DECLARE @RET INT = -1
	
	IF @GUILD_UID > 0 AND @MASTER_UID >= 0
	BEGIN

		IF (SELECT GUILD_UID FROM pangya.pangya_guild WHERE [GUILD_UID] = @GUILD_UID 
			AND ((@MASTER_UID = 0 AND GUILD_STATE IN(4,5)) OR (@MASTER_UID > 0 AND GUILD_STATE = 4)) 
			AND (GUILD_CLOSURE_DATE IS NULL OR getdate() < GUILD_CLOSURE_DATE)) IS NOT NULL
		BEGIN

			IF @MASTER_UID = 0 -- GM reabriu a guild do player
			BEGIN
			
				DECLARE @GUILD_MASTER INT = (SELECT GUILD_LEADER FROM pangya.pangya_guild WHERE [GUILD_UID] = @GUILD_UID)

				IF @GUILD_MASTER IS NOT NULL
				BEGIN

					UPDATE
						pangya.pangya_guild
							SET GUILD_STATE = IIF(GUILD_MARK_IMG_IDX = 0, 0 /*Pending*/, 1/*Now Open*/),
								GUILD_CLOSURE_DATE = NULL
					WHERE [GUILD_UID] = @GUILD_UID

					-- Insert Activity Player
					INSERT INTO pangya.pangya_guild_atividade_player(GUILD_UID, [UID], [FLAG])
					VALUES(@GUILD_UID, @GUILD_MASTER, 20)

					-- Sucesso
					SET @RET = 1

				END

			END
			ELSE
			BEGIN
			
				UPDATE
					pangya.pangya_guild
						SET GUILD_STATE = IIF(GUILD_MARK_IMG_IDX = 0, 0 /*Pending*/, 1/*Now Open*/),
							GUILD_CLOSURE_DATE = NULL
				WHERE [GUILD_UID] = @GUILD_UID AND GUILD_LEADER = @MASTER_UID

				-- Insert Activity Player
				INSERT INTO pangya.pangya_guild_atividade_player(GUILD_UID, [UID], [FLAG])
				VALUES(@GUILD_UID, @MASTER_UID, 19)

				-- Sucesso
				SET @RET = 1

			END

		END
		ELSE
			SET @RET = -2	-- a guild não está fechada
	END

	SELECT @RET AS RET
END
