CREATE PROCEDURE [pangya].[ProcInsertMascot]
	-- Add the parameters for the stored procedure here
	@IDUSER INT,
	@ITEMTYPEID INT,
	@TIPO TINYINT,
	@ISCASH TINYINT,
	@TEMPO INT,
	@MESSAGE NVARCHAR(100),
	@PRICE INT
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;

    DECLARE @ITEM_ID INT = -1

	IF @ITEMTYPEID > 0 AND (SELECT [UID] FROM pangya.account WHERE [UID] = @IDUSER) IS NOT NULL
	BEGIN
		DECLARE @regDT datetime2 = getdate()
		DECLARE @endDT datetime2 = @regDT

		SET @ITEM_ID = (SELECT item_id FROM pangya.pangya_mascot_info WHERE [UID] = @IDUSER AND typeid = @ITEMTYPEID AND valid = 1)

		IF @ITEM_ID > 0 AND @ITEM_ID IS NOT NULL
		BEGIN
			DECLARE @diff INT = 0
			
			SELECT @diff = case when DATEDIFF(SECOND, getdate(), EndDate) > 0 THEN 1 ELSE 0 END FROM pangya.pangya_mascot_info WHERE [UID] = @IDUSER AND item_id = @ITEM_ID
			 
			UPDATE pangya.pangya_mascot_info SET [Message] = @MESSAGE, EndDate = DATEADD(DAY, @TEMPO, (CASE WHEN @diff = 1 THEN EndDate ELSE getdate() END))
			WHERE [UID] = @IDUSER AND item_id = @ITEM_ID
		END
		ELSE
		BEGIN

			IF @TEMPO > 0
				SET @endDT = DATEADD(DAY, @TEMPO, @regDT)

			INSERT INTO pangya.pangya_mascot_info(UID, typeid, valid, tipo, IsCash, RegDate, EndDate, Period, [Message], Price)
				VALUES(@IDUSER, @ITEMTYPEID, 1, @TIPO, @ISCASH, @regDT, @endDT, @TEMPO, @MESSAGE, @PRICE)

			SET @ITEM_ID = @@IDENTITY
		END
	END

	SELECT @ITEM_ID AS ITEM_ID
END
GO
