      *   M2SS0134: Conversion of following Comment(s) is not supported :  Jogou VS, Camp e etc de 18 holes pode salvar record de pangs e score
      *
      */

      IF @flag = 1
         BEGIN

            SET @TipoRet = 
               (
                  SELECT 
                     CASE 
                        WHEN (pangya_record.best_score = 127 OR pangya_record.best_score > @best_score_in) OR pangya_record.best_pang < @best_pang_in THEN 1
                        ELSE 0
                     END
                  FROM pangya.pangya_record
                  WHERE 
                     pangya_record.UID = @IDUSER AND 
                     pangya_record.course = @course_in AND 
                     pangya_record.tipo = @tipo_in AND 
                     pangya_record.assist = @assist_in
               )

            /*
            *   SSMA informational messages:
            *   M2SS0134: Conversion of following Comment(s) is not supported :  add 1000 pangs por que ele fez record de pangs e score(um dos 2 ou os 2)
            *
            */

            IF @TipoRet = 1
               UPDATE pangya.user_info
                  SET 
                     pang = user_info.Pang + 1000
               WHERE user_info.UID = @IDUSER

            UPDATE pangya.pangya_record
               SET 
                  best_score = 
                     CASE 
                        WHEN (pangya_record.best_score = 127 OR pangya_record.best_score > @best_score_in) THEN @best_score_in
                        ELSE pangya_record.best_score
                     END, 
                  best_pang = 
                     CASE 
                        WHEN pangya_record.best_pang < @best_pang_in THEN @best_pang_in
                        ELSE pangya_record.best_pang
                     END, 
                  character_typeid = 
                     CASE 
                        WHEN @TipoRet = 1 THEN @character_typeid_in
                        ELSE pangya_record.character_typeid
                     END
            WHERE 
               pangya_record.UID = @IDUSER AND 
               pangya_record.course = @course_in AND 
               pangya_record.tipo = @tipo_in AND 
               pangya_record.assist = @assist_in

         END

      UPDATE pangya.pangya_record
         SET 
            tacada = pangya_record.tacada + @tacada_in, 
            putt = pangya_record.putt + @putt_in, 
            hole = pangya_record.hole + @hole_in, 
            fairway = pangya_record.fairway + @fairway_in, 
            holein = pangya_record.holein + @holein_in, 
            puttin = pangya_record.puttin + @puttin_in, 
            total_score = pangya_record.total_score + @best_score_in, 
            event_score = @event_score_in
      WHERE 
         pangya_record.UID = @IDUSER AND 
         pangya_record.course = @course_in AND 
         pangya_record.tipo = @tipo_in AND 
         pangya_record.assist = @assist_in

