	@PURCHASE TINYINT,
	@TEMPO INT
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;

    DECLARE @ITEM_ID INT = -1

	IF @ITEMTYPEID > 0 AND (SELECT [UID] FROM pangya.account WHERE [UID] = @IDUSER) IS NOT NULL
	BEGIN
		SET @ITEM_ID = ISNULL((SELECT item_id FROM pangya.pangya_item_warehouse WHERE [UID] = @IDUSER AND typeid = @ITEMTYPEID AND valid = 1), -1)

