			UPDATE pangya.pangya_item_warehouse SET EndDate = DATEADD(DAY, @TEMPO, (case when @diff = 1 THEN EndDate ELSE getdate() END)), C3 = C3 + @TEMPO
			WHERE [UID] = @IDUSER AND item_id = @ITEM_ID 
		END
		ELSE
		BEGIN
			IF @TEMPO > 0
				SET @endDT = DATEADD(DAY, @TEMPO, getdate())

