CREATE PROCEDURE [pangya].[ProcMakeGuild]
	-- Add the parameters for the stored procedure here
	@NAME NVARCHAR(20),
	@MASTER INT,
	@CONDITION_LEVEL SMALLINT,
	@INTRO NVARCHAR(50),
	@MASTER_COMMENT NVARCHAR(50)
AS
BEGIN

	DECLARE @GUILD_UID INT = -1
	
	IF @NAME != N'' AND @MASTER != 0
	BEGIN

		DECLARE @OTHER_GUILD INT = (
			SELECT
				a.Guild_UID
			FROM
				pangya.account a
				INNER JOIN
				pangya.pangya_guild b
				ON a.Guild_UID = b.GUILD_UID
			WHERE a.[UID] = @MASTER AND 
				(b.GUILD_STATE NOT IN(4, 5) OR b.GUILD_CLOSURE_DATE IS NULL OR getdate() < b.GUILD_CLOSURE_DATE)
		)

		IF @OTHER_GUILD IS NULL
		BEGIN

			IF DATEDIFF(HOUR, ISNULL((SELECT MAX(REG_DATE) FROM pangya.pangya_guild_atividade_player WHERE [UID] = @MASTER AND [FLAG] = 8/*Create*/), DATEADD(DAY, -1, getdate())), getdate()) >= 24/*1 Dia*/
			BEGIN

				DECLARE @PANG BIGINT = (SELECT pang FROM pangya.user_info WHERE [UID] = @MASTER)
		
				-- Verifica se o player tem os 5k de pangs para criar a guild
				IF @PANG IS NOT NULL AND @PANG >= 5000
				BEGIN

					-- Delete ele de qualquer membro de guild que ele esteja, que o OTHER_GUILD é null, se ele estava em uma guild, ela já está fechada
					DELETE FROM pangya.pangya_guild_member WHERE [MEMBER_UID] = @MASTER

					-- Cria a Guild
					INSERT INTO pangya.pangya_guild(GUILD_NAME, GUILD_LEADER, GUILD_CONDITION_LEVEL, GUILD_INFO, GUILD_NOTICE, GUILD_NEW_MARK_IDX) 
					VALUES(@NAME, @MASTER, @CONDITION_LEVEL, @INTRO, @MASTER_COMMENT, 1)

					SET @GUILD_UID = @@IDENTITY

					-- Insert Member of guild
					INSERT INTO pangya.pangya_guild_member(GUILD_UID, MEMBER_UID, MEMBER_STATE_FLAG) VALUES(@GUILD_UID, @MASTER, 1/*Master*/)

					-- Update player account  GUILD_UID
					UPDATE
						pangya.account
							SET Guild_UID = @GUILD_UID
					WHERE [UID] = @MASTER

					-- Insert Log Activety of player guild
					INSERT INTO pangya.pangya_guild_atividade_player(GUILD_UID, [UID], [FLAG]) VALUES(@GUILD_UID, @MASTER, 8/*Criou uma Guild*/)

					-- Tira os 5k de pang que gastou para criar a guild
					UPDATE
						pangya.user_info
							SET pang = @PANG - 5000
					WHERE [UID] = @MASTER

					-- Sucesso
					SELECT GUILD_UID, GUILD_NEW_MARK_IDX FROM pangya.pangya_guild WHERE GUILD_UID = @GUILD_UID

				END
				ELSE
					SET @GUILD_UID = -3	-- Não tem pangs suficiente para criar a guild

			END
			ELSE
				SET @GUILD_UID = -2	-- Não pode criar a guild, faz menos de 24 horas que ele criou outra guild

		END
		ELSE
			SET @GUILD_UID = -4	-- Player já é membro de outra guild, saia dela para poder criar outra guild

	END

	SELECT @GUILD_UID AS GUILD_UID
END
GO
