-- =============================================
CREATE PROCEDURE [pangya].[ProcRegisterGachaJPPlayerWin]
	-- Add the parameters for the stored procedure here
	@UID INT,
	@COUNT_ITEM INT,
	@STR VARCHAR(8000)
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	IF @UID > 0 AND @COUNT_ITEM > 0 AND @STR IS NOT NULL AND @STR != ''
	BEGIN
		
		DECLARE @FIRSTID INT = -1
		DECLARE @ITEM VARCHAR(50)
		DECLARE @ITEM_TYPEID INT
		DECLARE @ITEM_QNTD INT
		DECLARE @ITEM_RARITY INT
		DECLARE @ITEM_GACHA_NUM INT
		DECLARE @ITEM_COUNT INT = 0
		DECLARE @ITEM_ID INT = -1
				
		-- Cursor
		DECLARE itens CURSOR FOR
			SELECT Item FROM dbo.DelimitedSplit8K(@STR, N'|')

		OPEN itens
		FETCH NEXT FROM itens INTO @ITEM
		WHILE @@FETCH_STATUS = 0
		BEGIN

			IF @ITEM IS NOT NULL
			BEGIN
				
				SET @ITEM_TYPEID = (SELECT Item FROM dbo.DelimitedSplit8K(@ITEM, N';') WHERE ItemNumber = 1)
				SET @ITEM_QNTD = (SELECT Item FROM dbo.DelimitedSplit8K(@ITEM, N';') WHERE ItemNumber = 2)
				SET @ITEM_RARITY = (SELECT Item FROM dbo.DelimitedSplit8K(@ITEM, N';') WHERE ItemNumber = 3)
				SET @ITEM_GACHA_NUM = (SELECT Item FROM dbo.DelimitedSplit8K(@ITEM, N';') WHERE ItemNumber = 4)

				IF @ITEM_TYPEID IS NOT NULL AND @ITEM_QNTD IS NOT NULL AND @ITEM_RARITY IS NOT NULL AND @ITEM_GACHA_NUM IS NOT NULL
				BEGIN
							
					INSERT INTO pangya.pangya_gacha_jp_player_win([UID], typeid, qnty, rarity_type, gacha_num, valid, send_mail)
					VALUES(@UID, @ITEM_TYPEID, @ITEM_QNTD, @ITEM_RARITY, @ITEM_GACHA_NUM, 0, 0) -- Ativo depois se der tudo certo
					
					IF @FIRSTID = -1
						SET @FIRSTID = @@IDENTITY

					-- Itens inseridos no mail
					SET @ITEM_COUNT = @ITEM_COUNT + 1
				END
			END

			FETCH NEXT FROM itens INTO @ITEM

		END

		-- CLOSE AND DEALLOCATE CURSOR
		CLOSE itens
		DEALLOCATE itens

		-- Verifica se foi os itens tudo OK
		IF @COUNT_ITEM = @ITEM_COUNT AND @COUNT_ITEM = (SELECT COUNT_BIG([index]) FROM pangya.pangya_gacha_jp_player_win WHERE [UID] = @UID AND [index] >= @FIRSTID)
		BEGIN
			
			-- Tudo ok adiciona libera os itens
			UPDATE pangya.pangya_gacha_jp_player_win SET valid = 1 WHERE [UID] = @UID AND valid = 0 AND [index] >= @FIRSTID

			-- Retorna o primeiro ID do ITEM
			SELECT @FIRSTID AS FIRST_ID

		END
		ELSE
		BEGIN

			-- Deleta todos os que adicionou que não conseguiu adicionar o itens do player
			DELETE FROM pangya.pangya_gacha_jp_player_win 
			WHERE [UID] = @UID AND valid = 0 AND [index] >= @FIRSTID
		END
	END
END
