-- =============================================
-- Author:		<Author,,Name>
-- Create date: <Create Date,,>
-- Description:	<Description,,>
-- =============================================
CREATE PROCEDURE [pangya].[ProcRegisterLuciaAttendanceReward] 
	-- Add the parameters for the stored procedure here
	@UID INT,
	@COUNT_ITEM INT,
	@STR VARCHAR(8000)
AS
BEGIN
	
	IF @UID > 0 AND @COUNT_ITEM > 0 AND @STR IS NOT NULL AND @STR != ''
	BEGIN
		
		DECLARE @MSGID INT = 0
		DECLARE @LOGID INT = 0
		DECLARE @ITEM VARCHAR(50)
		DECLARE @ITEM_TYPEID INT
		DECLARE @ITEM_QNTD INT
		DECLARE @ITEM_COUNT INT = 0
		DECLARE @ITEM_ID INT = -1

		-- Make Mail in gift table
		INSERT INTO pangya.pangya_gift_table([UID], fromid, [message], giftdate, Lida_YN, valid, Flag, Contador_Vista)
		VALUES(@UID, N'@SUPERSS', N'Lucia Attendance reward', getdate(), 0, 0/*Não válido, depois eu libero ele se estiver tudo certo*/, 0, 0)

		SET @MSGID = @@IDENTITY

		IF @MSGID > 0
		BEGIN

			-- Make Log
			INSERT INTO pangya.pangya_lucia_attendance_reward_log([UID], MSG_ID) VALUES(@UID, @MSGID)

			SET @LOGID = @@IDENTITY

			IF @LOGID > 0
			BEGIN
				
				-- Cursor
				DECLARE itens CURSOR FOR
					SELECT Item FROM dbo.DelimitedSplit8K(@STR, N'|')

				OPEN itens
				FETCH NEXT FROM itens INTO @ITEM
				WHILE @@FETCH_STATUS = 0
				BEGIN

					IF @ITEM IS NOT NULL
					BEGIN
				
						SET @ITEM_TYPEID = (SELECT Item FROM dbo.DelimitedSplit8K(@ITEM, N';') WHERE ItemNumber = 1)
						SET @ITEM_QNTD = (SELECT Item FROM dbo.DelimitedSplit8K(@ITEM, N';') WHERE ItemNumber = 2)

						IF @ITEM_TYPEID IS NOT NULL AND @ITEM_QNTD IS NOT NULL
						BEGIN
							
							EXEC pangya.ProcInsertItemNoEmail 0, @UID, @MSGID, @ITEM_ID, @ITEM_TYPEID, 0, @ITEM_QNTD, 0
						
							-- Itens inseridos no mail
							SET @ITEM_COUNT = @ITEM_COUNT + 1
						END
					END

					FETCH NEXT FROM itens INTO @ITEM

				END

				-- CLOSE AND DEALLOCATE CURSOR
				CLOSE itens
				DEALLOCATE itens

				-- Verifica se foi os itens tudo OK
				IF @COUNT_ITEM = @ITEM_COUNT AND @COUNT_ITEM = (SELECT COUNT_BIG(Msg_ID) FROM pangya.pangya_item_mail WHERE [Msg_ID] = @MSGID)
				BEGIN
			
					-- Tudo ok adiciona os 50CP para o player e libera o mail e envia a msg que um mail foi enserido
					UPDATE pangya.user_info SET Cookie = Cookie + 50 WHERE [UID] = @UID

					UPDATE pangya.pangya_gift_table SET valid = 1 WHERE Msg_ID = @MSGID

					-- Comando para mostrar no jogo que o player recebeu um presente
					EXEC pangya.ProcInsertCommand 4, @UID, @MSGID, 0, 0, 0, 1, 0, 1, null

					-- Retorna a MSG ID e o LOG ID
					SELECT @MSGID AS MSG_ID, @LOGID AS LOG_ID

				END
				ELSE
				BEGIN
					-- Error ao salvar os itens, excluí ele e o mail e registra no log do lucia attendance que deu erro
					DELETE FROM pangya.pangya_item_mail WHERE Msg_ID = @MSGID

					DELETE FROM pangya.pangya_gift_table WHERE Msg_ID = @MSGID

					UPDATE pangya.pangya_lucia_attendance_reward_log SET [ERROR] = N'FAIL TO INSERT ITENS' WHERE [index] = @LOGID

					--PRINT '-UM-'
				END

			END
			ELSE
			BEGIN
				-- Erro ao criar o log, deleta o mail criado
				DELETE FROM pangya.pangya_gift_table WHERE Msg_ID = @MSGID

				--PRINT '-DOIS-'
			END
		END
	END
END
