CREATE PROCEDURE [pangya].[ProcRequestJoinGuild]
	-- Add the parameters for the stored procedure here
	@PLAYER_UID INT,
	@GUILD_UID INT
AS
BEGIN

	DECLARE @RET INT = -1
	
	IF @PLAYER_UID > 0 AND @GUILD_UID > 0
	BEGIN
		
		DECLARE @OTHER_GUILD INT = (
			SELECT
				a.Guild_UID
			FROM
				pangya.account a
				INNER JOIN
				pangya.pangya_guild b
				ON a.Guild_UID = b.GUILD_UID
			WHERE a.[UID] = @PLAYER_UID AND 
				(b.GUILD_STATE NOT IN(4, 5) OR b.GUILD_CLOSURE_DATE IS NULL OR getdate() < b.GUILD_CLOSURE_DATE)
		)

		IF @OTHER_GUILD IS NULL OR @OTHER_GUILD = @GUILD_UID
		BEGIN

			IF (SELECT GUILD_UID FROM pangya.pangya_guild WHERE [GUILD_UID] = @GUILD_UID AND GUILD_PERMITION_JOIN = 1 AND GUILD_STATE IN(0, 1) AND GUILD_CLOSURE_DATE IS NULL) IS NOT NULL
			BEGIN

				IF (SELECT MEMBER_UID FROM pangya.pangya_guild_member WHERE GUILD_UID = @GUILD_UID AND MEMBER_UID = @PLAYER_UID) IS NULL
				BEGIN

					-- Delete ele de qualquer membro de guild que ele esteja, que o OTHER_GUILD é null, se ele estava em uma guild, ela já está fechada
					DELETE FROM pangya.pangya_guild_member WHERE [MEMBER_UID] = @PLAYER_UID

					-- Adiciona ele para a nova Guild
					INSERT pangya.pangya_guild_member(GUILD_UID, MEMBER_UID, MEMBER_MSG, MEMBER_STATE_FLAG, GUILD_PANG, GUILD_POINT)
					VALUES (@GUILD_UID, @PLAYER_UID, '', 9, 0, 0)

					INSERT pangya.pangya_guild_atividade_player([UID], GUILD_UID, FLAG)
					VALUES (@PLAYER_UID, @GUILD_UID, 1)

					UPDATE pangya.account
						SET 
							GUILD_UID = @GUILD_UID
					WHERE account.[UID] = @PLAYER_UID

					SET @RET = 1

				END
				ELSE
					SET @RET = -2	-- Já fez o pedido para entrar nessa guild
				END

			ELSE
				SET @RET = -3	-- A guild que ele quer entrar está fechada ou bloqueada

		END
		ELSE
			SET @RET = -4	-- Player já está em outra guild

	END

	SELECT @RET AS RET
END
