CREATE PROCEDURE [pangya].[ProcRescueID]
	-- Add the parameters for the stored procedure here
	@EMAIL NVARCHAR(100)
AS
BEGIN
	
	DECLARE @RET INT = -1
	DECLARE @KEY NVARCHAR(100) = N''
	DECLARE @INDEX BIGINT = 0
	DECLARE @UID INT = 0

	IF @EMAIL != N''
	BEGIN

		SET @UID = (SELECT [UID] FROM pangya.contas_beta WHERE email = @EMAIL)

		IF @UID IS NOT NULL AND @UID > 0
		BEGIN
			
			IF (SELECT TOP(1) [UID] FROM pangya.pangya_rescue_pwd_log WHERE [UID] = @UID AND tipo = 2 AND [state] = 0 AND DATEADD(HOUR, 1, send_date) >= getdate()) IS NULL
			BEGIN

				INSERT INTO pangya.pangya_rescue_pwd_log([UID], tipo) VALUES(@UID, 2/*ID*/)

				SET @INDEX = @@IDENTITY

				SELECT @KEY = key_uniq FROM pangya.pangya_rescue_pwd_log WHERE [index] = @INDEX AND [UID] = @UID
				
				-- Sucesso ao criar o log e a chave
				SET @RET = 1

			END
			ELSE
				SET @RET = -4;	-- Já enviou um email com as instruções para recuperar a senha

		END
		ELSE
			SET @RET = -3	-- Email invalido

	END

	SELECT @RET AS RET, @UID AS [UID], @INDEX AS [INDEX], @KEY AS [KEY], @EMAIL as EMAIL
END
