CREATE PROCEDURE [pangya].[ProcSendGachaJPPlayerItemToMail]
	-- Add the parameters for the stored procedure here
	@UID INT,
	@ID BIGINT
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	IF @UID > 0 AND @ID > 0
	BEGIN

		IF (SELECT top 1 [index] FROM pangya.pangya_gacha_jp_player_win WHERE [UID] = @UID AND [index] = @ID AND valid = 1 AND send_mail = 0) IS NOT NULL
		BEGIN

			DECLARE @MSGID INT = 0

			-- Make Mail in gift table
			INSERT INTO pangya.pangya_gift_table([UID], fromid, [message], giftdate, Lida_YN, valid, Flag, Contador_Vista)
			VALUES(@UID, N'@SUPERSS', N'Gacha Item', getdate(), 0, 0/*Não válido, depois eu libero ele se estiver tudo certo*/, 0, 0)

			SET @MSGID = @@IDENTITY

			IF @MSGID > 0
			BEGIN
				DECLARE @ITEM_ID INT = -1
				DECLARE @ITEM_TYPEID INT = 0
				DECLARE @ITEM_QNTD INT = 0
				
				SELECT top 1 @ITEM_TYPEID = typeid, @ITEM_QNTD = qnty
				FROM pangya.pangya_gacha_jp_player_win 
				WHERE [UID] = @UID AND [index] = @ID AND valid = 1 AND send_mail = 0

				-- ADD Item
				EXEC pangya.ProcInsertItemNoEmail 0, @UID, @MSGID, @ITEM_ID, @ITEM_TYPEID, 0, @ITEM_QNTD, 0

				-- Verifica se adicionou
				IF (SELECT COUNT_BIG(Msg_ID) FROM pangya.pangya_item_mail WHERE [Msg_ID] = @MSGID) = 1
				BEGIN
					
					UPDATE pangya.pangya_gacha_jp_player_win SET send_mail = 1 WHERE [UID] = @UID AND [index] = @ID

					UPDATE pangya.pangya_gift_table SET valid = 1 WHERE Msg_ID = @MSGID

					-- Comando para mostrar no jogo que o player recebeu um presente
					EXEC pangya.ProcInsertCommand 4, @UID, @MSGID, 0, 0, 0, 1, 0, 1, null

					-- Retorna a MSG ID e o LOG ID
					SELECT @MSGID AS MSG_ID

				END
				ELSE
				BEGIN
					-- Error ao salvar os itens, excluí ele e o mail
					DELETE FROM pangya.pangya_item_mail WHERE Msg_ID = @MSGID

					DELETE FROM pangya.pangya_gift_table WHERE Msg_ID = @MSGID
				END
			END
		END

	END
END
