*
*/

CREATE PROCEDURE [pangya].[ProcTransfereMasteryPts]  
   @IDUSER int,
   @From_Taqueira_ID int,
   @To_Taqueira_ID int,
   @Quantidade int
AS 
   BEGIN

      SET  XACT_ABORT  ON

      SET  NOCOUNT  ON

      DECLARE
         @MASTERY int, 
         @MASTERY_TOTAL int, 
         @CALC int

      SELECT @MASTERY = pangya_item_warehouse.Mastery_Pts
      FROM pangya.pangya_item_warehouse
      WHERE pangya_item_warehouse.UID = @IDUSER AND pangya_item_warehouse.item_id = @From_Taqueira_ID

      SELECT @MASTERY_TOTAL = pangya_item_warehouse.Total_Mastery_Pts
      FROM pangya.pangya_item_warehouse
      WHERE pangya_item_warehouse.UID = @IDUSER AND pangya_item_warehouse.item_id = @From_Taqueira_ID

      SET @CALC = @MASTERY - (300 * @Quantidade)

      SET @MASTERY_TOTAL = 
         CASE 
            WHEN @CALC < 0 THEN @MASTERY
            ELSE (300 * @Quantidade)
         END

      IF @CALC < 0
         BEGIN

            UPDATE pangya.pangya_item_warehouse
               SET 
                  Mastery_Pts = 0, 
                  Total_Mastery_Pts = pangya_item_warehouse.Total_Mastery_Pts - @MASTERY_TOTAL
            WHERE pangya_item_warehouse.UID = @IDUSER AND pangya_item_warehouse.item_id = @From_Taqueira_ID

            UPDATE pangya.pangya_item_warehouse
               SET 
                  Mastery_Pts = pangya_item_warehouse.Mastery_Pts + @MASTERY_TOTAL, 
                  Total_Mastery_Pts = pangya_item_warehouse.Total_Mastery_Pts + @MASTERY_TOTAL
            WHERE pangya_item_warehouse.UID = @IDUSER AND pangya_item_warehouse.item_id = @To_Taqueira_ID

         END
      ELSE 
         BEGIN

            UPDATE pangya.pangya_item_warehouse
               SET 
                  Mastery_Pts = @CALC, 
                  Total_Mastery_Pts = pangya_item_warehouse.Total_Mastery_Pts - @MASTERY_TOTAL
            WHERE pangya_item_warehouse.UID = @IDUSER AND pangya_item_warehouse.item_id = @From_Taqueira_ID

            UPDATE pangya.pangya_item_warehouse
               SET 
                  Mastery_Pts = pangya_item_warehouse.Mastery_Pts + @MASTERY_TOTAL, 
                  Total_Mastery_Pts = pangya_item_warehouse.Total_Mastery_Pts + @MASTERY_TOTAL
            WHERE pangya_item_warehouse.UID = @IDUSER AND pangya_item_warehouse.item_id = @To_Taqueira_ID

         END

   END
